</code></pre>
<h2>The function that gives warnings</h2>
<pre><code>import * as dfd from "danfojs-node";
import Papa from "papaparse";

function decodeStockQuote(listOf150StkStr) {
  const strOfAll = listOf150StkStr.join("");

  const parsed = Papa.parse(strOfAll, {
    delimiter: ",",
    skipEmptyLines: true
  });

  let quoteDf = new dfd.DataFrame(parsed.data, {
    columns: "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefg".split("")
  });

  quoteDf.rename({
    mapper: {
      A: "STK",
      B: "TOpen",
      C: "TPCLOSE",
      D: "TPrice",
      E: "THigh",
      F: "TLow",
      I: "TVol",
      J: "TAmt",
      e: "TDate",
      f: "TTime"
    },
    axis: 1,
    inplace: true
  });

  quoteDf = quoteDf.loc({
    columns: ["STK", "TPrice", "TPCLOSE", "TOpen", "THigh", "TLow", "TVol", "TAmt", "TDate", "TTime"]
  });

  quoteDf.addColumn("TClose", quoteDf["TPrice"].values, { inplace: true });
  quoteDf.addColumn("RT", quoteDf["TPrice"].apply(v =&gt; v).div(quoteDf["TPCLOSE"]).sub(1).mul(100), { inplace: true });
  quoteDf.addColumn("TVol", quoteDf["TVol"].apply(x =&gt; x / TVOL_SCALE), { inplace: true });
  quoteDf.addColumn("TAmt", quoteDf["TAmt"].apply(x =&gt; x / TAMT_SCALE), { inplace: true });
  quoteDf.addColumn("STK_ID", quoteDf["STK"].apply(x =&gt; x.slice(13, 19)), { inplace: true });
  quoteDf.addColumn("STK_Name", quoteDf["STK"].apply(x =&gt; x.slice(21, 30)), { inplace: true });
  quoteDf.addColumn("TDate", quoteDf["TDate"].apply(x =&gt; x.slice(0, 4) + x.slice(5, 7) + x.slice(8, 10)), { inplace: true });

  return quoteDf;
}
</code></pre>
<h2>More warning messages</h2>
<pre class="lang-none prettyprint-override"><code>Warning about assigning values on a sliced DataFrame (line with TVol)
Warning about assigning values on a sliced DataFrame (line with TAmt)
Warning about assigning values on a sliced DataFrame (line with TDate)
